class MsgStore
{
  constructor()
  {
    this.msgs = [];
    this.subject = new Rx.Subject();
  }

  getObservable()
  {
    return this.subject.asObservable();
  }

  onCmd(action)
  {
    switch(action.cmd)
    {
      case 'displayMsgs':
        this.msgs.push({text: action.params[0], sender:0}, {text: action.params[1], sender:1});
        break;
      case 'clear':
        this._onClear();
        break;
      case 'sendMsg':
        this.msgs.push({text: action.params[0], sender:0});
        break;
      case 'receiveMsg':
        this.msgs.push({text: action.params[0], sender:1});
        break;
      default: return;
    }
    this.subject.next();
  }

  _onClear()
  {
    this.msgs = [];
    this.subject.next();
  }

}


class CmdDispatcher {
  constructor() {
    this.subject = new Rx.Subject();
  }

  getObservable() {
    return this.subject.asObservable();
  }

  dispatch(cmd, params) {
    this.subject.next({ cmd: cmd, params: params });
  }
}
this.store = new MsgStore();
this.dispatcher = new CmdDispatcher();
var _this = this;
function render() {
  ReactDOM.render(React.createElement(ConversationView, _this.store), _this.ui);
}

this.store.getObservable().subscribe({
  next: render,
  error: console.log,
  complete: () => console.log('completed')
});

this.dispatcher.getObservable().subscribe({
  next: data => _this.store.onCmd(data),
  error: console.log
});

render();
